package oopsConcept;

public class A {
	public void methodA() {
		System.out.println("Base class method");
	}
	class B extends A{
		public void methodB() {
			System.out.println("Child class method");
	   }

	}
	
		public static void main(String[] args) {
			// TODO Auto-generated method stub
			A obj=new A();
			obj.methodA();
		
			
      }
		
		
}		
		
	


